import { Component, OnInit } from '@angular/core';
import { TaskService } from '../task.service';

export interface Task {
  title: string;
  completed: boolean;
}

@Component({
  selector: 'app-task-list',
  templateUrl: './task-list.component.html',
  styleUrls: ['./task-list.component.css']
})

export class TaskListComponent implements OnInit {
  todoList: Task[] = [];
  newTask: Task = { title: '', completed: false };
  filter: string = 'all';

  constructor(private taskService: TaskService) { }

  ngOnInit() {
    this.todoList = this.taskService.getTasks();
    // Carga las tareas desde algún lugar y actualiza las propiedades de totalTasks y completedTasks
    this.totalTasks = this.todoList.length;
    this.completedTasks = this.todoList.filter(task => task.completed).length;
  }

  addTask() {
    this.taskService.addTask(this.newTask);
    this.newTask = { title: '', completed: false };
  }

  deleteTask(index: number) {
    this.taskService.deleteTask(index);
  }

  updateTask(index: number, task: Task) {
    this.taskService.updateTask(index, task);
  }

  totalTasks: number = 0;
  completedTasks: number = 0;

  // tasks: Task[] = [
  //   // { title: 'Tarea 1', completed: false },
  //   // { title: 'Tarea 2', completed: true },
  //   // { title: 'Tarea 3', completed: false },
  //   // { title: 'Tarea 4', completed: true },
  //   // { title: 'Tarea 5', completed: false }
  // ];

  filterTasks(status: 'completadas' | 'pendientes') {
    if (status === 'completadas') {
      this.todoList = this.todoList.filter(task => task.completed);
    } else if (status === 'pendientes') {
      this.todoList = this.todoList.filter(task => !task.completed);
    }
  }

  // newTask: Task = { title: '', completed: false };

  // addTask() {
  //   if (this.newTask.title.trim().length === 0) {
  //     return;
  //   }

  //   this.tasks.push(this.newTask);

  //   this.newTask = { title: '', completed: false };
  // }

  // removeTask(task: { title: string; completed: boolean; }): void {
  //   const index = this.tasks.indexOf(task);
  //   if (index !== -1) {
  //     this.tasks.splice(index, 1);
  //   }
  // }

  // constructor() { }
}
