import { Component, OnInit } from '@angular/core';

interface Task {
  title: string;
  completed: boolean;
}

@Component({
  selector: 'app-task-list',
  templateUrl: './task-list.component.html',
  styleUrls: ['./task-list.component.css']
})
export class TaskListComponent implements OnInit {

  tasks: Task[] = [
    { title: 'Barrer', completed: true },
    { title: 'Trapear', completed: false },
    { title: 'Cocinar', completed: true },
    { title: 'Almorzar', completed: true },
  ];

  filterTasks(status: 'completadas' | 'pendientes') {
    if (status === 'completadas') {
      this.tasks = this.tasks.filter(task => task.completed);
    } else if (status === 'pendientes') {
      this.tasks = this.tasks.filter(task => !task.completed);
    }
  }

  constructor() { }

  ngOnInit() {
  }
}
